from torch.nn.utils import weight_norm


class Chomp1d(nn.Module):
    def __init__(self, chomp_size):
        super(Chomp1d, self).__init__()
        self.chomp_size = chomp_size

    def forward(self, x):
        return x[:, :, :-self.chomp_size].contiguous()

class TemporalBlock(nn.Module):
    def __init__(self, n_inputs, n_outputs,n_layers, kernel_size, stride, dropout=0.2):
        super(TemporalBlock, self).__init__()

        self.net = nn.Sequential()
        for i in range(n_layers):
            dilation = 2**i
            padding = (i+1) * (kernel_size-1)

            self.net.add_module("conv{}".format(i),nn.Conv1d(n_inputs, n_outputs, kernel_size,
                                           stride=stride, padding=padding, dilation=dilation))
            self.net.add_module("chomp{}".format(i),Chomp1d(padding))
            self.net.add_module("relu{}".format(i),nn.ReLU())
            self.net.add_module("dropout{}".format(i),nn.Dropout(dropout))

            n_inputs = n_outputs
